/******************************************************************************
 * Data specific to sink component
 *****************************************************************************/
/**
 * Sourcce Component State Machine.
 * Sourcce component shall be in GFXSRC_STATE_RESET by default. The state machine
 * for sink component is as follows:
 *
 * Current State        Command             New State
 * ------------------------------------------------------------
 * GFXSRC_STATE_RESET    MSYS_COMP_CMD_INIT       GFXSRC_STATE_INIT
 * GFXSRC_STATE_INIT     MSYS_COMP_CMD_CONFIG     GFXSRC_STATE_READY
 * GFXSRC_STATE_READY    MSYS_COMP_CMD_CONFIG     GFXSRC_STATE_READY
 * GFXSRC_STATE_READY    MSYS_COMP_CMD_START      GFXSRC_STATE_RUNNING
 * GFXSRC_STATE_READY    MSYS_COMP_CMD_DEINIT     GFXSRC_STATE_RESET
 * GFXSRC_STATE_RUNNING  MSYS_COMP_CMD_STOP       GFXSRC_STATE_READY
 * GFXSRC_STATE_RUNNING  MSYS_COMP_CMD_DEINIT     GFXSRC_STATE_RESET
 */
typedef uint32_t GfxSrc_StateType;
#define GFXSRC_STATE_RESET   (0UL)   /**< Sourcce component Reset state */
#define GFXSRC_STATE_INIT    (1UL)   /**< Sourcce component Init state */
#define GFXSRC_STATE_READY   (2UL)   /**< Sourcce component Ready state */
#define GFXSRC_STATE_RUNNING (3UL)   /**< Sourcce component Running state */

/* Sourcce component even masks */
/** Event mask for Sourcce component's command Queue */
#define GFXSRC_CMDQ_EVENT        SystemEvent0
/** Event mask for all the input ports for Sourcce component */
#define GFXSRC_INPORT_EVENTS     (SystemEvent8)

/** Maximum number of Channels for Sourcce component's input port */
#define GFXSRC_CHANNEL_MAX       (1UL)

/** Structure to hold Sourcce component information */
typedef struct _GfxSrc_CompType {
    GfxSrc_StateType state;
    uint32_t dataBufCnt;    /**< Number of Data Buffers processed */
    uint32_t curMBIndex;
} GfxSrc_CompType;

static GfxSrc_CompType GfxSrc_Comp = {
    .state = GFXSRC_STATE_RESET,
    .dataBufCnt = 0UL,
    .curMBIndex = 0UL,
};

/******************************************************************************
 * Sourcce <==> Framework interaction
 *
 * Interface for component<-->framework interactions are defined in component.h
 * Framwork uses a message queue to send commands to a component, where
 * framework shall be the client and component shall be the server. Any
 * component shall support the following commands from the framework:
 *  1) MSYS_COMP_CMD_INIT
 *  2) MSYS_COMP_CMD_CONFIG
 *  3) MSYS_COMP_CMD_START
 *  4) MSYS_COMP_CMD_STOP
 *  5) MSYS_COMP_CMD_DEINIT
 *****************************************************************************/

/** Define and initialize MSGQ_HdrQType instance for Sourcce component */
MSGQ_DEFINE_HDRQ(GfxSrc_CmdMsgHdrQ);

/** Array for commands to Sourcce component */
MSYS_CmdType GfxSrc_CmdMsg[MSGQ_SIZE];

/** GetMessage function to get the ith message */
void * GfxSrc_GetCmdMsg(uint32_t i)
{
    void *msg = NULL;
    if (MSGQ_SIZE > i) {
        msg = (void *) &GfxSrc_CmdMsg[i];
    }

    return msg;
}

/* Define the command queue for the sink component, on which framework shall
 * post the different commands for the sink component.
 */
MSGQ_DEFINE (GfxSrc_CmdQ, GfxSrc_Task, GFXSRC_CMDQ_EVENT, MSGQ_CLIENT_SIGNAL_ENABLE, MSYS_CmdType,
             &GfxSrc_CmdMsgHdrQ, GfxSrc_GetCmdMsg);

/* Framework shall use this API to post a command to this sink component */
int32_t GfxSrc_SendCmd(const MSYS_CmdType *const aCmd, const uint32_t aMask,
                      const MSGQ_MsgHdrType** const aHdr)
{
    int32_t retVal = BCM_ERR_INVAL_PARAMS;

    if ((NULL != aCmd) && (MSYS_COMP_ID_GFXSRC == aCmd->compID) && (NULL != aHdr)) {
        /* Activate the sink component task if an INIT is received */
        if (MSYS_COMP_CMD_INIT == aCmd->cmd) {
            BCM_ActivateTask(GfxSrc_Task);
        }

        /* Post the command to the Sourcce component's command queue */
        retVal = MSGQ_SendMsg(&GfxSrc_CmdQ, (const void * const)aCmd, aMask, aHdr);
    }
    return retVal;
}

/* Framework shall use this API to get the response of a command that was
 * posted to this Sourcce component
 */
int32_t GfxSrc_RecvResp(const MSGQ_MsgHdrType* const aHdr, MSYS_CmdType *const aCmd)
{
    int32_t retVal;

    if ((NULL != aHdr) && (NULL != aCmd)) {
        /* Receive response from the Sourcce component's command queue */
        retVal = MSGQ_RecvMsg(&GfxSrc_CmdQ, aHdr, (void* const)aCmd);
    } else {
        retVal = BCM_ERR_INVAL_PARAMS;
    }

    return retVal;
}

#define GFXSRC_MAX_NUM_OUT_PORT (1UL) /* Number of output ports */

/* Framework shall use this API to get the number of output ports for this sink
 * component.
 */
uint32_t GfxSrc_GetNumMediaOutPort(void)
{
    /* Since this is a sink component, there are no output ports */
    return GFXSRC_MAX_NUM_OUT_PORT;
}


static MSYS_BufferType GfxSrc_MediaBuffer[MSGQ_SIZE];

void *GfxSrc_GetDataMsg(uint32_t i)
{
    void *msg = NULL;
    if (MSGQ_SIZE > i) {
        msg = (void *) &GfxSrc_MediaBuffer[i];
    }
    return msg;
}
MSYS_DECLARE_OUT_PORT(GfxSrc_OutPort, 0UL, GfxSrc_DataMSGQ_HdrQType,
                        GfxSrc_Task, GfxSrc_GetDataMsg,
                        MSYS_COMP_ID_GFXSRC, SystemEvent8);

/* Framework shall use this API to get the MSYS_OutPortType of this component where
 * MSYS_FillBuffer() request shall be posted (if any).
 */
const MSYS_OutPortType* GfxSrc_GetMediaOutPort(const uint32_t aIndex)
{
    const MSYS_OutPortType *port = NULL;
    /* Since this is sink component there are no output ports */
    switch (aIndex) {
    case 0UL:
        port = &GfxSrc_OutPort;
        break;
    default:
        break;
    }
    return port;
}

static const MSYS_CompFnTblType GfxSrc_CompFnTable =
{
    GfxSrc_SendCmd,
    GfxSrc_RecvResp,
    GfxSrc_GetMediaOutPort,
    NULL,
};

const MSYS_CompFnTblType* GfxSrc_GetCompFnTable(void)
{
    return &GfxSrc_CompFnTable;
}

/******************************************************************************
 * Sourcce component Functionality.
 *
 * Sourcce component uses a single task, that shall be the server task for the
 * framework<-->component message queue. The same task handles all interaction
 * with other components as well.
 *****************************************************************************/

/* Helper function that handles all transitions from Reset state */
static void GfxSrc_ResetStateHandler()
{
    int32_t retVal;
    uint32_t cmdQIdx;
    MSYS_CmdType *processCmd = NULL;

    /* Try to get any command from the Framework if any */
    retVal = MSGQ_GetMsgIdx(&GfxSrc_CmdQ, &cmdQIdx);
    if (BCM_ERR_OK == retVal) {
        processCmd = &GfxSrc_CmdMsg[cmdQIdx];
        if (MSYS_COMP_CMD_INIT == processCmd->cmd) {
            GfxSrc_Comp.state = GFXSRC_STATE_INIT;
            processCmd->response = BCM_ERR_OK;
        } else {
            processCmd->response = BCM_ERR_UNINIT;
        }
        /* Mark the command as completed */
        retVal = MSGQ_CompleteMsgIdx(&GfxSrc_CmdQ, cmdQIdx);
        if (retVal != BCM_ERR_OK) {
            /* Report error */
        }
    }
}

/* Helper function that handles all transitions from Init state */
static void GfxSrc_InitStateHandler()
{
    int32_t retVal;
    uint32_t cmdQIdx;
    MSYS_CmdType *processCmd = NULL;

    /* Try to get any command from the FrameWork if any */
    retVal = MSGQ_GetMsgIdx(&GfxSrc_CmdQ, &cmdQIdx);
    if (BCM_ERR_OK == retVal) {
        processCmd = &GfxSrc_CmdMsg[cmdQIdx];
        switch (processCmd->cmd) {
        case MSYS_COMP_CMD_CONFIG:
            /* Configure this component with processCmd->CfgData.
             * In this example we do not have anything to configure.
             */
            GfxSrc_Comp.state = GFXSRC_STATE_READY;
            processCmd->response = BCM_ERR_OK;
            break;
        case MSYS_COMP_CMD_DEINIT:
            /* Deinitialize this component.
             */
            GfxSrc_Comp.state = GFXSRC_STATE_RESET;
            processCmd->response = BCM_ERR_OK;
            break;
        default:
            processCmd->response = BCM_ERR_INVAL_STATE;
            break;
        }
        retVal = MSGQ_CompleteMsgIdx(&GfxSrc_CmdQ, cmdQIdx);
        if (retVal != BCM_ERR_OK) {
            /* Report error */
        }
    }
}

/* Helper function that handles all transitions from Ready state */
static void GfxSrc_ReadyStateHandler()
{
    int32_t retVal;
    uint32_t cmdQIdx;
    MSYS_CmdType *processCmd = NULL;

    /* Try to get any command from the FrameWork if any */
    retVal = MSGQ_GetMsgIdx(&GfxSrc_CmdQ, &cmdQIdx);
    if (BCM_ERR_OK == retVal) {
        processCmd = &GfxSrc_CmdMsg[cmdQIdx];
        switch(processCmd->cmd) {
        case MSYS_COMP_CMD_START:
            GfxSrc_Comp.state = GFXSRC_STATE_RUNNING;
            GfxSrc_Comp.curMBIndex = 0UL;
            GfxSrc_Comp.dataBufCnt = 0UL;
            /* Signal the input port of this Sourcce component to start processing */
            BCM_SetEvent(GfxSrc_Task, GFXSRC_INPORT_EVENTS);
            processCmd->response = BCM_ERR_OK;
            break;
        case MSYS_COMP_CMD_CONFIG:
            /* Configure this component with processCmd->CfgData.
             * In this example we do not have anything to configure.
             */
            processCmd->response = BCM_ERR_OK;
            break;
        case MSYS_COMP_CMD_DEINIT:
            GfxSrc_Comp.state = GFXSRC_STATE_RESET;
            processCmd->response = BCM_ERR_OK;
            break;
        default:
            processCmd->response = BCM_ERR_INVAL_STATE;
            break;
        }
        retVal = MSGQ_CompleteMsgIdx(&GfxSrc_CmdQ, cmdQIdx);
        if (retVal != BCM_ERR_OK) {
            /* Report error */
        }
    }
}

